choose.when:
select * from (select A.*,rownum R from (select projectNum,category,detailcategory,name,state,email,del_check,budget,to_char(reg_date,'YYYY-MM-DD') reg_date from project where state in
#{item}
and category like '%' || #{projectDTO.category} || '%' and detailcategory like '%' || #{projectDTO.detailCategory} || '%' and name like '%' || #{projectDTO.name} || '%' and email like '%' || #{projectDTO.email} || '%' and planstate like '%' || #{projectDTO.planState} || '%' and startdate like '%' || #{projectDTO.startDate} || '%' and finishdate like '%' || #{projectDTO.finishDate} || '%' and reg_date between #{projectDTO.reg_date} and sysdate
and del_check in ('1','2')
) A) B where R between #{listInfo.startRow} and #{listInfo.lastRow} order by reg_date asc

choose.otherwise:
select * from (select A.*,rownum R from (select projectNum,category,detailcategory,name,state,email,del_check,budget,to_char(reg_date,'YYYY-MM-DD') reg_date from project where state in
#{item}
and category like '%' || #{projectDTO.category} || '%' and detailcategory like '%' || #{projectDTO.detailCategory} || '%' and name like '%' || #{projectDTO.name} || '%' and startdate like '%' || #{projectDTO.startDate} || '%' and finishdate like '%' || #{projectDTO.finishDate} || '%' and reg_date between #{projectDTO.reg_date} and sysdate and projectNum in
#{item}
) A) B where R between #{listInfo.startRow} and #{listInfo.lastRow} order by reg_date asc

mapper.update:
update project set 

   state='done' where projectNum=#{projectNum}


   state='ing' where projectNum=#{projectNum}


   state='cancel',del_check=2 where projectNum=#{projectNum}


   state='payFinish' where projectNum=#{projectNum}

choose.otherwise:
select count(projectNum) from (select * from project where state in
#{item}
and category like '%' || #{projectDTO.category} || '%' and detailcategory like '%' || #{projectDTO.detailCategory} || '%' and name like '%' || #{projectDTO.name} || '%' and startdate like '%' || #{projectDTO.startDate} || '%' and finishdate like '%' || #{projectDTO.finishDate} || '%' and reg_date between #{projectDTO.reg_date} and sysdate and projectNum in
#{item}
)order by reg_date asc
